const s=()=>{const e=new Date;let a=e.getFullYear(),t=e.getMonth()+1,l=e.getDate(),d=e.getDay(),r=["(日)","(月)","(火)","(水)","(木)","(金)","(土)"][d],n=e.getHours(),o=e.getMinutes(),c=e.getSeconds();t=t<10?"0"+t:t,l=l<10?"0"+l:l,n=n<10?"0"+n:n,o=o<10?"0"+o:o,c=c<10?"0"+c:c;let u=`${a}.${t}.${l} ${r}`,y=`${n}:${o}:${c}`;document.querySelector(".clock-date").innerText=u,document.querySelector(".clock-time").innerText=y};setInterval(s,1e3);
